import React, { Component } from "react";
import { Link } from "react-router-dom";
import { auth } from "../config/firebase.utils";
import "../styles/LoginStyles.css";

const INITIAL_STATE = {
  username: "",
  email: "",
  password: "",
  message: "",
  touched: {
    username: false,
    email: false,
    password: false,
  },
};

class SignUp extends Component {
  constructor(props) {
    super(props);
    this.state = INITIAL_STATE;
  }

  handleSubmit = (event) => {
    event.preventDefault();

    auth()
      .createUserWithEmailAndPassword(this.state.email, this.state.password)
      .then((user) => {
        this.setState({ ...INITIAL_STATE });
        this.props.history.push("/login");
      })
      .catch((err) => {
        this.setState({ message: err.message });
        console.log(err);
      });
  };

  handleChange = (event) => {
    event.persist();
    this.setState({ [event.target.name]: event.target.value });
  };

  handleBlur = (field) => (event) => {
    this.setState({
      touched: { ...this.state.touched, [field]: true },
    });
  };

  validate = (username, email, password) => {
    const errors = {
      username: "",
      email: "",
      password: "",
    };

    if (this.state.touched.username && username.length < 3)
      errors.username = "Username characters should be >= 3";
    else if (this.state.touched.username && username.length > 15)
      errors.username = "Username characters should be <=15";

    const emailReg = /^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/;
    if (this.state.touched.email && !emailReg.test(email))
      errors.email = "Entered email is invalid";

    const passwordReg = /^(?=.*[a-z])(?=.*[!@#$%^&*])(?=.{5,})$/;
    if (this.state.touched.password && !passwordReg.test(password))
      errors.password =
        "Password should have special character & length should be >= 5";

    return errors;
  };

  render() {
    const errors = validate(username, email, password);

    return (
      <div className="box">
        <div className="box__header">
          <h1>SignUp</h1>
        </div>
        <span>{this.state.message.length ? this.state.message : null}</span>
        <form onSubmit={this.handleSubmit}>
          <div className="inputbox">
            <input
              type="text"
              className="username"
              name="username"
              onBlur={this.handleBlur("username")}
              value={this.state.username}
              onChange={this.handleChange}
              required
            />
            <label>User Name</label>
            <span className="errors">{errors.username}</span>
          </div>
          <div className="inputbox">
            <input
              type="text"
              className="email"
              name="email"
              onBlur={this.handleBlur("email")}
              value={this.state.email}
              onChange={this.handleChange}
              required
            />
            <label>Email</label>
          </div>
          <span className="errors">{errors.email}</span>
          <div className="inputbox">
            <input
              type="password"
              className="password"
              name="password"
              onBlur={this.handleBlur("password")}
              value={this.state.password}
              onChange={this.handleChange}
              required
            />
            <label>Password</label>
            <span className="errors">{errors.password}</span>
          </div>
          {/* <div className="inputbox">
            <input type="password" className="password" required />
            <label>Confirm Password</label>
          </div> */}
          <button className="submit-button" type="submit" value="SignUp">
            SignUp
          </button>
        </form>
        <span className="">
          Already have an Account?&nbsp;&nbsp;
          <Link to="/login">Login</Link>
        </span>
      </div>
    );
  }
}

export default SignUp;
